import React, { useState } from "react";
import "./Form.css";

const Form = () => {
  const [patientData, setPatientData] = useState({
    HN: "",
    name: "",
    surname: "",
    gender: "",
    prefix: "",
    DOB: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPatientData({ ...patientData, [name]: value });
  };

  const fetchPatientData = async (hn) => {
    try {
      const response = await fetch(`http://localhost:3000/api/v1/patients/get?HN=${hn}`);
      const data = await response.json();
      setPatientData({
        HN: data.HN,
        name: data.name,
        surname: data.surname,
        gender: data.gender,
        prefix: data.prefix,
        DOB: data.DOB,
      });
    } catch (error) {
      console.error("Error fetching patient data:", error);
    }
  };

  const handleHNBlur = () => {
    if (patientData.HN) {
      fetchPatientData(patientData.HN);
    }
  };

  return (
    <section className="container-form">
      <header>บันทึกอาการรายวัน ประจำวันที่ {new Date().toLocaleDateString()}</header>

      <form action="#" className="form">
        <div className="input-box">
          <label>Hospital Number (HN) </label>
          <input
            type="text"
            placeholder="Enter hospital number"
            name="HN"
            value={patientData.HN}
            onChange={handleInputChange}
            onBlur={handleHNBlur}
            required
          />
        </div>
        <div className="column">
          <div className="input-box">
            <label>ชื่อ</label>
            <input
              type="text"
              placeholder="ชื่อ"
              name="name"
              value={patientData.name}
              readOnly
            />
          </div>
          <div className="input-box">
            <label>สกุล</label>
            <input
              type="text"
              placeholder="สกุล"
              name="surname"
              value={patientData.surname}
              readOnly
            />
          </div>
        </div>
        <div className="input-box">
          <label>เพศ</label>
          <input
            type="text"
            placeholder="เพศ"
            name="gender"
            value={patientData.gender}
            readOnly
          />
        </div>
        <div className="input-box">
          <label>วันเดือนปีเกิด (DOB)</label>
          <input
            type="text"
            placeholder="วันเดือนปีเกิด"
            name="DOB"
            value={patientData.DOB}
            readOnly
          />
        </div>
        <div className="input-box">
          <label>คำนำหน้า</label>
          <input
            type="text"
            placeholder="คำนำหน้า"
            name="prefix"
            value={patientData.prefix}
            readOnly
          />
        </div>

        {/* Other form fields */}
      </form>
    </section>
  );
};

export default Form;
