import React, { useState } from "react";
import "./Form.css";

const Form = () => {
  const [patientData, setPatientData] = useState({
    HN: "",
    BT: "",
    BP: "",
    HR: "",
    RR: "",
    O2sat: "",
    conscious: "",
    breath_pattern: "",
    extra_symptoms: "",
    eat_method: "",
    food_type: "",
    extra_food: "",
    food_intake: [],
    sleep: "",
    excretion: "",
    notes: "",
  });

  const [submittedData, setSubmittedData] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPatientData({
      ...patientData,
      [name]: value
    });
  };

  const handleFoodIntakeChange = (e) => {
    const options = e.target.options;
    const selectedOptions = Array.from(options)
      .filter(option => option.selected)
      .map(option => option.value);
    setPatientData({
      ...patientData,
      food_intake: selectedOptions
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:3000/api/v1/patients/record", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(patientData),
      });

      if (response.ok) {
        const result = await response.json();
        setSubmittedData(result);
        alert("Record submitted successfully!");
      } else {
        console.error("Failed to submit record");
      }
    } catch (error) {
      console.error("Error submitting record:", error);
    }
  };

  return (
    <section className="container-form">
      <header>บันทึกอาการรายวัน ประจำวันที่ {new Date().toLocaleDateString()}</header>

      <form className="form" onSubmit={handleSubmit}>
        <div className="input-box">
          <label>Hospital Number (HN)</label>
          <input 
            type="text" 
            name="HN"
            placeholder="Enter hospital number" 
            value={patientData.HN} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>BT (อุณหภูมิ)</label>
          <input 
            type="text" 
            name="BT"
            placeholder="ไม่มีไข้, ไข้ต่ำ, ไข้สูง" 
            value={patientData.BT} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>BP (ความดันโลหิต)</label>
          <input 
            type="text" 
            name="BP"
            placeholder="ปกติ, ต่ำ, สูง" 
            value={patientData.BP} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>HR (อัตราการเต้นของหัวใจ)</label>
          <input 
            type="text" 
            name="HR"
            placeholder="ปกติ, ช้า, เร็ว" 
            value={patientData.HR} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>RR (อัตราการหายใจ)</label>
          <input 
            type="text" 
            name="RR"
            placeholder="ปกติ, ช้า, เร็ว" 
            value={patientData.RR} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>O2sat (ค่าออกซิเจนในเลือด)</label>
          <input 
            type="text" 
            name="O2sat"
            placeholder="ปกติ, ต่ำ" 
            value={patientData.O2sat} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>ระดับความรู้สึกตัว</label>
          <input 
            type="text" 
            name="conscious"
            placeholder="ตื่น รู้สึกตัวดี, หลับ, ซึม, สับสน, ไม่รู้สึกตัว" 
            value={patientData.conscious} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>ลักษณะการหายใจ</label>
          <input 
            type="text" 
            name="breath_pattern"
            placeholder="หายใจปกติ, หายใจช้า, หายใจเร็ว หายใจหอบเหนื่อย" 
            value={patientData.breath_pattern} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>อาการเบื้องต้น</label>
          <input 
            type="text" 
            name="extra_symptoms"
            placeholder="กรุณาอธิบายอาการเพิ่มเติม" 
            value={patientData.extra_symptoms} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>รูปแบบการรับประทานอาหาร</label>
          <input 
            type="text" 
            name="eat_method"
            placeholder="รับประทานเองได้, ใส่สายยางให้อาหาร" 
            value={patientData.eat_method} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>อาหาร</label>
          <input 
            type="text" 
            name="food_type"
            placeholder="นมแม่, นมผสม, อาหารปกติ, อาหารอ่อน, อาหารเหลว" 
            value={patientData.food_type} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>อาหารเสริม</label>
          <input 
            type="text" 
            name="extra_food"
            placeholder="กรุณาอธิบายอาหารเสริมเพิ่มเติม" 
            value={patientData.extra_food} 
            onChange={handleInputChange} 
          />
        </div>

        <div className="input-box">
          <label>การรับประทานอาหาร (เลือกหลายตัวเลือกได้)</label>
          <select 
            multiple 
            name="food_intake"
            value={patientData.food_intake} 
            onChange={handleFoodIntakeChange}
          >
            <option value="ข้าว">ข้าว</option>
            <option value="ผลไม้">ผลไม้</option>
            {/* Add more options as needed */}
          </select>
        </div>

        <div className="input-box">
          <label>การนอนหลับ</label>
          <input 
            type="text" 
            name="sleep"
            placeholder="นอนหลับได้, หลับๆตื่นๆ, นอนไม่หลับ" 
            value={patientData.sleep} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>การขับถ่าย</label>
          <input 
            type="text" 
            name="excretion"
            placeholder="ดี, ไม่ดี" 
            value={patientData.excretion} 
            onChange={handleInputChange} 
            required 
          />
        </div>

        <div className="input-box">
          <label>หมายเหตุเพิ่มเติม</label>
          <textarea 
            name="notes"
            placeholder="แจ้งอาการหรือผลการตรวจอื่นๆที่ต้องการแจ้งเพิ่มเติม" 
            value={patientData.notes} 
            onChange={handleInputChange} 
          />
        </div>

        <button type="submit">บันทึก</button>
      </form>

      {submittedData && (
        <section className="submitted-data">
          <header>ข้อมูลที่บันทึก</header>
          <table className="submitted-table">
            <tbody>
              {Object.keys(submittedData).map((key) => (
                <tr key={key}>
                  <td>{key}</td>
                  <td>{submittedData[key]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      )}
    </section>
  );
};

export default Form;
