import React, { useState } from "react";
import "./Form.css";

const Form = () => {
  // State to store patient data
  const [patientData, setPatientData] = useState({
    HN: "",
    name: "",
    surname: "",
    gender: "",
    DOB: "",
    prefix: "",
  });

  // Function to handle HN input change
  const handleHNChange = async (e) => {
    const HN = e.target.value;

    // Update the HN field in state
    setPatientData((prevData) => ({
      ...prevData,
      HN,
    }));

    // Trigger the API call when 7 characters are entered
    if (HN.length === 7) {
      try {
        const response = await fetch(`http://localhost:3000/api/v1/patients/get?HN=${HN}`, {
          method: "GET",
          headers: {
            "Accept": "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();

          // Update the patientData state with the fetched data
          setPatientData((prevData) => ({
            ...prevData,
            HN: data.HN || prevData.HN,
            name: data.name || "",
            surname: data.surname || "",
            gender: data.gender || "",
            DOB: data.DOB || "",
            prefix: data.prefix || "",
          }));
        } else {
          console.error("Failed to fetch patient data");
        }
      } catch (error) {
        console.error("Error fetching patient data:", error);
      }
    }
  };

  return (
    <section className="container-form">
      <header>บันทึกอาการรายวัน ประจำวันที่ {new Date().toLocaleDateString()}</header>

      <form className="form">
        <div className="input-box">
          <label>Hospital Number (HN) </label>
          <input 
            type="text" 
            placeholder="Enter hospital number" 
            value={patientData.HN} 
            onChange={handleHNChange} 
            required 
          />
        </div>

        <div className="column">
          <div className="input-box">
            <label>ชื่อ</label>
            <input 
              type="text" 
              placeholder="ชื่อ" 
              value={patientData.name} 
              readOnly 
            />
          </div>

          <div className="input-box">
            <label>สกุล</label>
            <input 
              type="text" 
              placeholder="สกุล" 
              value={patientData.surname} 
              readOnly 
            />
          </div>
        </div>

        <div className="input-box">
          <label>เพศ</label>
          <input 
            type="text" 
            placeholder="เพศ" 
            value={patientData.gender} 
            readOnly 
          />
        </div>

        <div className="input-box">
          <label>วันเกิด (DOB)</label>
          <input 
            type="text" 
            placeholder="วันเกิด" 
            value={patientData.DOB} 
            readOnly 
          />
        </div>

        <div className="input-box">
          <label>คำนำหน้า</label>
          <input 
            type="text" 
            placeholder="คำนำหน้า" 
            value={patientData.prefix} 
            readOnly 
          />
        </div>

        {/* Additional form fields go here */}

        <button type="submit">บันทึก</button>
      </form>
    </section>
  );
};

export default Form;
