import React, { useState } from "react";
import "./Form.css";

const Form = () => {
  const [hn, setHN] = useState(""); // Separate state for HN input
  const [patientData, setPatientData] = useState({
    firstName: "",
    lastName: "",
    gender: "",
    age: "",
  });

  const currentDate = new Date().toLocaleDateString();

  // Function to handle HN input change and fetch patient data
  const handleHNChange = async (e) => {
    const hnValue = e.target.value;
    setHN(hnValue);

    if (hnValue) {
      try {
        const response = await fetch(`http://localhost:3000/api/v1/patients/get?HN=${hnValue}`);
        const data = await response.json();
        
        if (data) {
          setPatientData({
            firstName: data.firstName || "",
            lastName: data.lastName || "",
            gender: data.gender || "",
            age: data.age || "",
          });
        }
      } catch (error) {
        console.error("Error fetching patient data:", error);
      }
    }
  };

  return (
    <section className="container-form">
      <header>บันทึกอาการรายวัน ประจำวันที่ {currentDate}</header>

      <form action="#" className="form">
        <div className="input-box">
          <label>Hospital Number (HN)</label>
          <input
            type="text"
            placeholder="Enter hospital number"
            value={hn}
            onChange={handleHNChange}
            required
          />
        </div>

        <div className="column">
          <div className="input-box">
            <label>ชื่อ</label>
            <input type="text" placeholder="ชื่อ" value={patientData.firstName} readOnly />
          </div>

          <div className="input-box">
            <label>สกุล</label>
            <input type="text" placeholder="สกุล" value={patientData.lastName} readOnly />
          </div>
        </div>

        <div className="input-box">
          <label>เพศ</label>
          <input type="text" placeholder="เพศ" value={patientData.gender} readOnly />
        </div>
        <div className="input-box">
          <label>อายุ</label>
          <input type="text" placeholder="อายุ" value={patientData.age} readOnly />
        </div>

        {/* Include the rest of the form fields here */}

        <button type="submit">บันทึก</button>
      </form>
    </section>
  );
};

export default Form;
