import React, { useState } from 'react';
import './Table.css';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

const mockPatients = [
  {
    HN: 'AI12345',
    prefix: 'Mr.',
    name: 'John',
    surname: 'Doe',
    gender: 'Male',
    DOB: '1990-01-01',
    records: [
      {
        id: 'rec1',
        timestamp: '2024-08-09T14:39:25.853Z',
      },
      {
        id: 'rec2',
        timestamp: '2024-08-09T16:39:25.853Z',
      },
      {
        id: 'rec3',
        timestamp: '2024-08-09T00:39:25.853Z',
      },
    ],
  },
  {
    HN: 'AI12346',
    prefix: 'Ms.',
    name: 'Jane',
    surname: 'Smith',
    gender: 'Female',
    DOB: '1992-02-02',
    records: [
      {
        id: 'rec4',
        timestamp: '2024-08-10T08:39:25.853Z',
      },
      {
        id: 'rec5',
        timestamp: '2024-08-10T15:39:25.853Z',
      },
      {
        id: 'rec6',
        timestamp: '2024-08-10T23:39:25.853Z',
      },
    ],
  },
];

const getShiftColor = (time) => {
  const hour = parseInt(time.split(':')[0], 10);
  if (hour >= 0 && hour < 8) return 'night-shift';
  if (hour >= 8 && hour < 16) return 'morning-shift';
  if (hour >= 16 && hour < 24) return 'evening-shift';
  return '';
};

const formatTimestamp = (timestamp) => {
  const date = new Date(timestamp);
  const dateString = date.toLocaleDateString();
  const timeString = date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  return { date: dateString, time: timeString };
};

const TableComponent = ({ setSelectedSidebarItem }) => {
  const [expandedRow, setExpandedRow] = useState(null);

  const toggleRow = (index) => {
    setExpandedRow(expandedRow === index ? null : index);
  };

  return (
    <div className="container-table">
      <div className="header-container">
        <div className="header-left">
          <div className="header-title">All Customers</div>
          <div className="header-link">Active Members</div>
        </div>
        <div className="header-right">
          <input
            type="text"
            className="search-input"
            placeholder="Search by Name or Hospital Number"
          />
          <button
            className="new-pt-button"
            onClick={() => setSelectedSidebarItem('Form')}
          >
            New PT
          </button>
        </div>
      </div>

      <TableContainer component={Paper}>
        <Table className="Table">
          <TableHead>
            <TableRow>
              <TableCell>Hospital Number</TableCell>
              <TableCell>Customer Name</TableCell>
              <TableCell>Date of Birth</TableCell>
              <TableCell>Gender</TableCell>
              <TableCell>Details</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {mockPatients.map((patient, index) => (
              <React.Fragment key={index}>
                <TableRow>
                  <TableCell>{patient.HN}</TableCell>
                  <TableCell>{`${patient.prefix} ${patient.name} ${patient.surname}`}</TableCell>
                  <TableCell>{patient.DOB}</TableCell>
                  <TableCell>{patient.gender}</TableCell>
                  <TableCell>
                    <button onClick={() => toggleRow(index)}>
                      {expandedRow === index ? 'Hide Details' : 'Show Details'}
                    </button>
                  </TableCell>
                </TableRow>
                {expandedRow === index &&
                  patient.records.map((record) => {
                    const { date, time } = formatTimestamp(record.timestamp);
                    return (
                      <TableRow key={record.id} className="sub-row">
                       
                            <TableCell>{date}</TableCell>
                            <TableCell className={`time-field ${getShiftColor(time)}`}>{time}</TableCell>
                          
                
                        <TableCell>
                              <a href={`/details/${record.id}`}>{patient.name}</a>
                            </TableCell>
                      </TableRow>
                    );
                  })}
              </React.Fragment>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default TableComponent;
