import React, { useState } from 'react';
import './Table.css';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

// ข้อมูลตัวอย่างของผู้ป่วย (Mock Data)
const mockPatients = [
  {
    HN: 'AI12345', // หมายเลขโรงพยาบาล
    prefix: 'Mr.', // คำนำหน้า
    name: 'John', // ชื่อ
    surname: 'Doe', // นามสกุล
    gender: 'Male', // เพศ
    DOB: '1990-01-01', // วันเกิด
    records: [ // ข้อมูลบันทึกของผู้ป่วย
      {
        id: 'rec1', // รหัสบันทึก
        timestamp: '2024-08-09T14:39:25.853Z', // เวลาที่บันทึก
      },
      {
        id: 'rec2',
        timestamp: '2024-08-09T16:39:25.853Z',
      },
      {
        id: 'rec3',
        timestamp: '2024-08-09T00:39:25.853Z',
      },
    ],
  },
  {
    HN: 'AI12346',
    prefix: 'Ms.',
    name: 'Jane',
    surname: 'Smith',
    gender: 'Female',
    DOB: '1992-02-02',
    records: [
      {
        id: 'rec4',
        timestamp: '2024-08-10T08:39:25.853Z',
      },
      {
        id: 'rec5',
        timestamp: '2024-08-10T15:39:25.853Z',
      },
      {
        id: 'rec6',
        timestamp: '2024-08-10T23:39:25.853Z',
      },
    ],
  },
];

const TableComponent = ({ setSelectedSidebarItem }) => {
  const [expandedRow, setExpandedRow] = useState(null); // ใช้จัดการแถวที่ขยายหรือย่อ

  // ฟังก์ชันสลับแถวระหว่างแถวที่ขยายและแถวที่ย่อ
  const toggleRow = (index) => {
    setExpandedRow(expandedRow === index ? null : index);
  };

  // ฟังก์ชันที่กำหนดสีพื้นหลังตามช่วงเวลา
  const getShiftColor = (timestamp) => {
    const hours = new Date(timestamp).getUTCHours();
    if (hours >= 16 && hours < 24) return 'evening'; // เวรบ่าย
    if (hours >= 0 && hours < 8) return 'night'; // เวรดึก
    if (hours >= 8 && hours < 16) return 'morning'; // เวรเช้า
    return ''; // ไม่มีช่วงเวลา
  };

  return (
    <div className="container-table">
      <div className="header-container">
        <div className="header-left">
          <div className="header-title">All Customers</div>
          <div className="header-link">Active Members</div>
        </div>
        <div className="header-right">
          <input
            type="text"
            className="search-input"
            placeholder="Search by Name or Hospital Number"
          />
          <button
            className="new-pt-button"
            onClick={() => setSelectedSidebarItem('Form')}
          >
            New PT
          </button>
        </div>
      </div>

      <TableContainer component={Paper}>
        <Table className="Table">
          <TableHead>
            <TableRow>
              <TableCell>Hospital Number</TableCell>
              <TableCell>Customer Name</TableCell>
              <TableCell>Date of Birth</TableCell>
              <TableCell>Gender</TableCell>
              <TableCell>Details</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {mockPatients.map((patient, index) => (
              <React.Fragment key={index}>
                {/* แถวของข้อมูลผู้ป่วย */}
                <TableRow>
                  <TableCell>{patient.HN}</TableCell>
                  <TableCell>{`${patient.prefix} ${patient.name} ${patient.surname}`}</TableCell>
                  <TableCell>{patient.DOB}</TableCell>
                  <TableCell>{patient.gender}</TableCell>
                  <TableCell>
                    <button onClick={() => toggleRow(index)}>
                      {expandedRow === index ? 'Hide Details' : 'Show Details'}
                    </button>
                  </TableCell>
                </TableRow>
                {/* แถวรายละเอียดของผู้ป่วย */}
                {expandedRow === index &&
                  patient.records.map((record) => (
                    <TableRow key={record.id} className={`sub-row ${getShiftColor(record.timestamp)}`}>
                      <TableCell colSpan={2}></TableCell>
                      <TableCell>{new Date(record.timestamp).toLocaleDateString()}</TableCell> {/* แสดงวันที่ */}
                      <TableCell>{patient.name}</TableCell>
                      <TableCell>
                        <a href={`/details/${record.id}`}>Details</a>
                      </TableCell>
                    </TableRow>
                  ))}
              </React.Fragment>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default TableComponent;
