import React, { useState } from "react";
import "./Form.css";


const Form = () => {
  const [HN, setHN] = useState("");
  const [records, setRecords] = useState([]);

  const handleHNChange = async (e) => {
    const HN = e.target.value;
    setHN(HN);

    if (HN.length === 7) {
      try {
        const response = await fetch(`http://localhost:3000/api/v1/patients/get?HN=${HN}`, {
          method: "GET",
          headers: {
            "Accept": "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();
          setRecords(data.records || []);
        } else {
          console.error("Failed to fetch records");
        }
      } catch (error) {
        console.error("Error fetching records:", error);
      }
    }
  };

  return (
    <section className="container-records">
      <header>ข้อมูลบันทึกประจำวัน</header>

      <div className="input-box">
        <label>Hospital Number (HN)</label>
        <input 
          type="text" 
          placeholder="Enter hospital number" 
          value={HN} 
          onChange={handleHNChange} 
          required 
        />
      </div>

      <table className="records-table">
        <thead>
          <tr>
            <th>Timestamp</th>
            <th>Name</th>
            <th>Details</th>
          </tr>
        </thead>
        <tbody>
          {records.map((record, index) => (
            <tr key={index}>
              <td>{record.timestamp}</td>
              <td>{record.name}</td>
              <td><a href={`/record/${record.id}`}>Details</a></td>
            </tr>
          ))}
        </tbody>
      </table>
    </section>
  );
};

export default Form;
